import "next-auth";
import "next-auth/jwt";

declare module "next-auth" {
  interface User {
    role: "admin" | "station";
    station_id?: string;
    station_name?: string;
    email?: string;
  }

  interface Session {
    user: {
      id?: string;
      name?: string | null;
      email?: string | null;
      image?: string | null;
      role: "admin" | "station";
      station_id?: string;
      station_name?: string;
    };
  }
}

declare module "next-auth/jwt" {
  interface JWT {
    role: "admin" | "station";
    station_id?: string;
    station_name?: string;
    email?: string;
  }
}
